# app.pyimport streamlit as stfrom sentence_transformers import SentenceTransformer, utilimport pandas as pdimport numpy as np# Charger le mod√®lemodel = SentenceTransformer('all-MiniLM-L6-v2')st.set_page_config(page_title="CoFounderMatch", page_icon="ü§ù", layout="centered")st.title("CoFounderMatch - AI Cofounder Matching")st.markdown("""Paste your short self-description and your potential cofounder's description below.The AI will compute a compatibility score and highlight potential collaboration strengths and risks.""")# Entr√©es utilisateuruser_profile = st.text_area("Your Profile", "", height=150)partner_profile = st.text_area("Potential Cofounder Profile", "", height=150)# Bouton pour calculer la compatibilit√©if st.button("Compute Compatibility"):    if user_profile.strip() == "" or partner_profile.strip() == "":        st.warning("Please fill in both profiles.")    else:        # Embeddings des profils        user_emb = model.encode(user_profile, convert_to_tensor=True)        partner_emb = model.encode(partner_profile, convert_to_tensor=True)        # Calcul de similarit√© cosinus        score = util.cos_sim(user_emb, partner_emb).item()        st.subheader("Compatibility Score")        st.write(f"{score*100:.1f}%")        # Analyse simplifi√©e des forces et risques        st.subheader("Potential Collaboration Strengths")        strengths = [            "- Shared skills and complementary experience",            "- Aligned professional goals",            "- Mutual openness to new ideas",            "- Good communication potential"        ]        for s in strengths:            st.write(s)        st.subheader("Potential Red Flags / Risks")        risks = [            "- Differences in working style",            "- Misaligned long-term vision",            "- Potential conflicts in decision-making",            "- Unequal availability or commitment"        ]        for r in risks:            st.write(r)# Section pour montrer des profils exemplesst.sidebar.header("Example Profiles")example_user = """Name: CamilleSkills: Product, UX, Digital MarketingGoals: Launch an innovative tech productPersonality: Creative, organized, open to new ideas"""example_partner = """Name: MarcoSkills: Growth, Sales, StrategyGoals: Scale a startup globallyPersonality: Analytical, driven, collaborative"""st.sidebar.subheader("Your Example Profile")st.sidebar.text(example_user)st.sidebar.subheader("Potential Cofounder Example")st.sidebar.text(example_partner)st.sidebar.markdown("""-Tip: Copy-paste these examples into the main text boxes to test the app.""")